        serverManager = EasyMock.mock(ServerManager.class);

        dh = EasyMock.createMockBuilder(DataHubImpl.class)
            .withConstructor()
            .createMock();
        dh.setServerManager(serverManager);
        dh.setHubConfig(hubConfig);

