xquery version "1.0-ml";

declare namespace flexrep = 'http://marklogic.com/xdmp/flexible-replication';

declare variable $FLEXREP-COLLECTION := "http://marklogic.com/xdmp/flexrep";

declare variable $domain-id := xs:unsignedLong("1432025117022463474");
declare variable $target-id := xs:unsignedLong("12831504965350255529");

declare function local:pull-delete(
  $domain-id as xs:unsignedLong,
  $target-id as xs:unsignedLong
) as empty-sequence()
{
  let $pull := local:pull-get($domain-id, $target-id, fn:true())
  return (
    local:_delete-domain-target-statuses($pull),
    xdmp:document-delete(xdmp:node-uri($pull))
  )
};

declare private function local:_delete-domain-target-statuses(
  $pull as element(flexrep:pull)
) as empty-sequence()
{
  for $s in fn:collection($FLEXREP-COLLECTION)
              /flexrep:domain-target-status
              [flexrep:domain-id = $pull/flexrep:domain-id and
               flexrep:target-id = $pull/flexrep:target-id]
  return xdmp:document-delete(xdmp:node-uri($s))
};

declare function local:pull-get(
  $domain-id as xs:unsignedLong,
  $target-id as xs:unsignedLong
) as element(flexrep:pull)?
{
  local:pull-get($domain-id, $target-id, fn:false())
};
  
declare function local:pull-get(
  $domain-id as xs:unsignedLong,
  $target-id as xs:unsignedLong,
  $assert as xs:boolean
) as element(flexrep:pull)*
{
  let $pull as element(flexrep:pull)? :=
    fn:collection($FLEXREP-COLLECTION)/flexrep:pull[flexrep:domain-id = $domain-id and flexrep:target-id = $target-id]
  return
    if (fn:exists($pull) or $assert = fn:false())
    then $pull
    else fn:error((), "FLEXREP-NOPULL", ("domain", $domain-id))
};

local:pull-delete($domain-id, $target-id)
