samplequery:
let $id := dbg:eval('1')
         let $stack := dbg:stack($id)
         let $status := dbg:status($id)
         let $expr := dbg:expr($id, fn:data($status/*:request[1]/*:expr-id))

         return (
         namespace-uri($stack),
         "
         ",
         local-name($stack),
         "
         ",
         fn:substring-before(fn:substring-after(xdmp:quote($stack), "stack xmlns=&quot;"), "&quot;>"),
         "
         ",
         namespace-uri($status),
         "
         ",
         local-name($status),
         "
         ",
         fn:substring-before(fn:substring-after(xdmp:quote($status), "requests xmlns=&quot;"), "&quot;>"),
         "
         ",
         namespace-uri($expr),
         "
         ",
         local-name($expr),
         "
         ",
         fn:substring-before(fn:substring-after(xdmp:quote($expr), "expr xmlns=&quot;"), "&quot;>"),
         "
         ",
         dbg:finish($id)
