import module namespace ptr="http://marklogic.com/qa" at "/journalarchivelib.xqy";
         let $log := xdmp:log("****Test3b running*****")

        let $query := 'xquery version "1.0-ml";
                       declare variable $ts as xs:dateTime external;
                       xdmp:node-replace(doc("/test.xml")/time-stamp, <time-stamp>{$ts}</time-stamp>);xdmp:sleep(6000)'

        let $doc := (  for $i in 1 to 10
                        let $ts:= xdmp:eval('xs:dateTime(fn:current-dateTime())')
                        return
                        ptr:eval-query($query,(xs:QName("ts"),$ts),"backup_1DB")
                    )

         let $log := xdmp:log("****Test3b running after load*****")

        let $query2:= 'xquery version "1.0-ml";
                        import module namespace ptr="http://marklogic.com/qa" at "/journalarchivelib.xqy";
                       declare variable $ts as xs:dateTime external;
                        try{ let $log :=xdmp:log("*** restore in Test3b started ***")
