        let $id:=ptr:eval-query($query2,(xs:QName("ts"),$ts),"backup_1DB")
        let $restore1 := ptr:wait-for-restore-to-complete($id,"backup_1DB",0)//*:status/text()
        let $rlback :=ptr:do-forest-rollback-to-safe-timestamp("backup_1DB",$id)
