        let $wait-forest2-to-open := ptr:wait-for-forest-restore-state-to-open("backup_1F2","backup_1DB",0)

        let $log := xdmp:log("*** restore completed***")
        let $res := xs:dateTime(ptr:eval-query('doc("/test.xml")//time-stamp/text()',(),"backup_1DB"))
       return
         ( $restore1," ", if($res < $ts) then "Restore successful" else (concat("Restore has some issue on doc timestamp: ",$res," ",$ts)),xdmp:log(concat("**** Test3 complete **** and Time stamps",$res," <  ",$ts)) )
